<style>
.clouds {
    position: absolute;
    bottom: 0;
    width: 100%;
    overflow: hidden;
    z-index: -1; 
    animation: rise 1s ease-out forwards;
}

.clouds svg {
  width: 100%;
  height: auto;
  display: block;
}

@keyframes rise {
  from {
    opacity: 0;
    transform: translateY(2em);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}


</style>

